import { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { ModalsDispatchContext } from '../modal/ModalsContext';

export default function useModals() {
	const { open, close } = useContext(ModalsDispatchContext);
	const history = useHistory();

	const openModal = (Component, props) => {
		// Record the modal opening state in the history
		history.push('/popup');

		// Open the modal
		open(Component, props);
	};

	const closeModal = (Component) => {
		// Close the modal
		close(Component);

		// Delay the history navigation to give time for the modal to close
		setTimeout(() => {
			// Remove the modal opening state from the history
			history.goBack();
		}, 0);
	};

	return {
		openModal,
		closeModal,
	};
}
