import { useContext } from 'react';
import { createHashHistory } from 'history';
import { ModalsDispatchContext } from '../modal/ModalsContext';

const history = createHashHistory();

export default function useModals() {
	const { open, close } = useContext(ModalsDispatchContext);

	const openModal = (Component, props) => {
		open(Component, props);
		history.push('/popup');
	};

	const closeModal = (Component) => {
		close(Component);

		// 현재 위치를 얻어와서 '/popup'이 포함되어 있다면 대체
		const currentLocation = history.location.pathname;
		if (currentLocation.includes('/popup')) {
			const previousLocation = currentLocation.replace('/popup', '');
			history.replace(previousLocation);
		}
	};

	return {
		openModal,
		closeModal,
	};
}
