import { useEffect, useContext, useState } from 'react';
import { createHashHistory } from 'history'; // history 패키지의 createHashHistory 함수를 사용
import { ModalsDispatchContext } from '../modal/ModalsContext';

const history = createHashHistory();

export default function useModals() {
	const { open, close } = useContext(ModalsDispatchContext);
	const [currentModal, setCurrentModal] = useState(null);

	const unlisten = history.listen((location, action) => {
		const currentPath = location.hash;
		if (currentPath !== '#/popup' && currentModal) {
			close(currentModal);
			setCurrentModal(null);
		}
	});
	useEffect(() => {
		return () => {
			unlisten();
		};
	}, [unlisten]);

	const openModal = (Component, props) => {
		// _ref가 존재하고, props가 존재하는지 확인
		if (Component && Component.props && Component.props._ref) {
			// 모달을 엽니다.
			open(Component, props);

			// 히스토리에 새로운 엔트리를 추가하고, 열린 모달 정보를 추적합니다.
			history.push('/popup');
			setCurrentModal(Component);
		} else {
			console.error('Invalid _ref or props:', Component);
		}
	};

	const closeModal = () => {
		history.goBack();
		close(currentModal);
		setCurrentModal(null);
	};

	return {
		openModal,
		closeModal,
	};
}
